import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';

const Signup = () => {
  const history = useHistory();
  const [name, setName] = useState('');
  const [aadhar, setAadhar] = useState('');
  const [password, setPassword] = useState('');

  const postData = async (e) => {
    e.preventDefault();

    const res = await fetch('/register', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name, aadhar, password }),
    });

    const data = await res.json();

    if (res.status === 422 || !data) {
      alert("Invalid Registration");
    } else {
      alert("Registration Successful");
      history.push('/login');
    }
  };

  return (
    <>
      <Container>
        <Form>
          <form method="POST">
            <div className="head">Signup</div>

            <div className="name">
              <label htmlFor="name">Name</label>
              <input type="text" name="name" id="name" placeholder="Enter your Name" value={name} onChange={(e) => setName(e.target.value)} />
            </div>

            <div className="aadhar">
              <label htmlFor="aadhar">Aadhar Number</label>
              <input type="text" name="aadhar" id="aadhar" placeholder="Enter your Aadhar Number" value={aadhar} onChange={(e) => setAadhar(e.target.value)} />
            </div>

            <div className="password">
              <label htmlFor="password">Password</label>
              <input type="password" name="password" id="password" placeholder="Enter your Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>

            <button type="submit" onClick={postData}>Register</button>
          </form>
        </Form>
      </Container>
    </>
  );
};

export default Signup;

const Container = styled.div`
  height: 89.3vh;
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #f5f7fa, #c3cfe2);
`;

const Form = styled.div`
  height: 90%;
  width: 35%;
  display: flex;
  justify-content: center;
  align-items: center;
  backdrop-filter: blur(10px);
  background: rgba(255, 255, 255, 0.8);
  border-radius: 20px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);

  form {
    width: 100%;
    display: grid;
    justify-content: center;
    text-align: center;
    padding: 2rem;

    .head {
      font-size: 2rem;
      font-weight: bold;
      color: #333;
      margin-bottom: 2rem;
      text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
    }

    .name, .aadhar, .password {
      margin: 1.5rem 0;
      text-align: left;
      label {
        font-size: 1rem;
        font-weight: bold;
        color: #333;
      }
      input {
        margin-top: 0.5rem;
        width: 100%;
        padding: 0.8rem;
        border: none;
        border-radius: 10px;
        box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      }
    }

    button {
      margin-top: 1.5rem;
      width: 50%;
      padding: 1rem;
      border: none;
      border-radius: 10px;
      background: #333;
      color: white;
      font-weight: bold;
      cursor: pointer;
      transition: background 0.3s;

      &:hover {
        background: #555;
      }
    }
  }
  
  @media all and (max-width: 828px) {
    width: 60%;
  }

  @media all and (max-width: 496px) {
    width: 90%;
  }
`;
