import React, { useEffect, useState } from "react";
import styled from "styled-components";

const Home = () => {
  const [userData, setUserData] = useState({});

  const loadData = async () => {
    const res = await fetch("/home", {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      credentials: "include",
    });
    const data = await res.json();
    setUserData(data);
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <>
      <Container>
        <Welcome>
          <div className="main">Welcome to the Online Voting System</div>
          <div className="down">
            <div className="one">scroll down</div>
            <div className="two">v</div>
          </div>
        </Welcome>
        <LiveFeed>
          <Head>
            <h1>Live Status</h1>
          </Head>
          <Stats>
            <Total>
              <h1>The total votes till date are:</h1>
              <h1 style={{ color: "blue" }}>{userData.total}</h1>
            </Total>
            <PartyWise>
              <h1 className="partyWise">PartyWise votes are:</h1>
              <Grid>
                <div className="party">
                  <h1>BJP</h1>
                  <img src="bjp.jpeg" alt="" />
                  <h2>{userData.bjp}</h2>
                </div>
                <div className="party">
                  <h1>AAP</h1>
                  <img src="aap.png" alt="" className="aap" />
                  <h2>{userData.aap}</h2>
                </div>
                <div className="party">
                  <h1>Congress</h1>
                  <img src="congress.png" alt="" />
                  <h2>{userData.congress}</h2>
                </div>
                <div className="party">
                  <h1>National Congress Party</h1>
                  <img src="inc.png" alt="" />
                  <h2>{userData.ncp}</h2>
                </div>
                <div className="party">
                  <h1>Indian National Lok Dal</h1>
                  <img src="inld.png" alt="" className="aap" />
                  <h2>{userData.inld}</h2>
                </div>
              </Grid>
            </PartyWise>
          </Stats>
        </LiveFeed>
      </Container>
    </>
  );
};

export default Home;

const Container = styled.div`
  width: 100%;
  background: #f0f2f5;
`;

const Welcome = styled.div`
  height: 89.3vh;
  width: 100%;
  display: grid;
  text-align: center;
  grid-template-rows: 50% 50%;
  background: url('3.jpg') no-repeat center center/cover;

  .main {
    font-size: 4.2rem;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #fff;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  }
  
  .down {
    display: flex;
    flex-direction: column;
    justify-content: end;
    align-items: center;
    padding-bottom: 1rem;

    .one {
      font-size: 1rem;
      color: #fff;
    }

    .two {
      animation-name: translate;
      animation-duration: 2s;
      animation-iteration-count: infinite;
      color: #fff;
    }
  }
`;

const LiveFeed = styled.div`
  padding: 2rem 0;
`;

const Head = styled.div`
  width: 100%;
  height: 20vh;
  display: flex;
  font-size: 2.5rem;
  justify-content: center;
  color: #ff5733;
  align-items: center;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
`;

const Stats = styled.div``;

const Total = styled.div`
  width: 100vw;
  height: 20vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
`;

const PartyWise = styled.div`
  width: 100vw;
  height: 100%;
  background: url(1.jpg) no-repeat center center/cover;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4rem;

  .partyWise {
    height: 10vh;
    color: #fff;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);

    @media all and (max-width: 500px) {
      font-size: 1.4rem;
    }
  }
  div {
    margin: 1rem;
    overflow: hidden;
    text-align: center;
    padding: 2rem;
    backdrop-filter: blur(13px);
    background: rgba(255, 255, 255, 0.2);
    border-radius: 10px;
    transition: transform 0.3s;

    &:hover {
      transform: scale(1.05);
    }
    
    h1, h2 {
      color: #fff;
    }
    
    h1 {
      font-size: 1.5rem;
    }

    img {
      margin-bottom: 2rem;
      height: 40%;
    }
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  width: 100%;

  @media all and (max-width: 1157px) {
    grid-template-columns: repeat(2, 1fr);
  }
  @media all and (max-width: 833px) {
    grid-template-columns: 1fr;
  }

  @media all and (max-width: 395px) {
    .aap {
      width: 8rem;
    }
  }
`;
